mapper.select:
        SELECT


--         级联表的字段
        ,jiaoshi.jiaoshi_name as jiaoshiName
        ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber
        ,jiaoshi.jiaoshi_phone as jiaoshiPhone
        ,jiaoshi.jiaoshi_photo as jiaoshiPhoto
        ,jiaoshi.jiaoshi_delete as jiaoshiDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_delete as yonghuDelete

        FROM keyanxiangmu  a
        left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.keyanxiangmu_uuid_number like CONCAT('%',#{params.keyanxiangmuUuidNumber},'%')


                and a.keyanxiangmu_name like CONCAT('%',#{params.keyanxiangmuName},'%')


                and a.keyanxiangmu_types = #{params.keyanxiangmuTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.jiaoshi_id is null
                    or
                    a.jiaoshi_id = #{params.jiaoshiId}
                )


                and a.keyanxiangmu_yesno_types = #{params.keyanxiangmuYesnoTypes}


                and a.shenheyijian_text like CONCAT('%',#{params.shenheyijianText},'%')


                and a.keyanxiangmu_content like CONCAT('%',#{params.keyanxiangmuContent},'%')


                  and a.keyanxiangmu_delete >= #{params.keyanxiangmuDeleteStart}   


                  and a.keyanxiangmu_delete <= #{params.keyanxiangmuDeleteEnd}   


                and a.keyanxiangmu_delete = #{params.keyanxiangmuDelete}




                and a.jiaoshi_id IS NOT NULL


                and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')


                and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')


                and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')


                  and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}   


                  and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}   


                and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


                and yonghu.yonghu_delete = #{params.yonghuDelete}



        order by a.${params.orderBy} desc
